var grailsFilterPane={showElement:function(e){try{if(typeof Effect!=="undefined"&&typeof Effect.Appear!=="undefined"&&$){if($(e)&&$(e).visible()===false){Effect.Appear(e,{duration:.5,queue:"end"})}}else{var t=document.getElementById(e);if(t&&t.style.display==="none"){t.style.display="block"}}}catch(n){alert(n)}return false},hideElement:function(e){if(typeof Effect!=="undefined"&&typeof Effect.Fade!=="undefined"){if($(e)&&$(e).visible()==true){Effect.Fade(e,{duration:.5,queue:"end"})}}else{var t=document.getElementById(e);if(t&&t.style.display!=="none"){t.style.display="none"}}return false},toggleElement:function(e){try{if(typeof Effect!=="undefined"&&typeof Effect.Appear!=="undefined"&&$){if($(e)&&$(e).visible()===false){Effect.Appear(e,{duration:.5,queue:"end"})}else if($(e)&&$(e).visible()===true){Effect.Fade(e,{duration:.5,queue:"end"})}}else{var t=document.getElementById(e);if(t&&t.style.display==="none"){t.style.display="block"}else if(t&&t.style.display==="block"){t.style.display="none"}}}catch(n){alert(n)}return false},clearFilterPane:function(e){var t=document.getElementById(e);var n;for(var r=0;r<t.elements.length;r++){n=t.elements[r];if(n&&n.name.indexOf("filter.")==0){if(n.type==="select-one"){n.selectedIndex=0}else if(n.type==="text"||n.type==="textarea"){n.value=""}}}},filterOpChange:function(e,t){if(e.slice(0,10)=="filter.op."){var n=e.substring(10);var r=document.getElementById(e);var i=r.options[r.selectedIndex];if(r){if(r.type==="select-one"){if(i.value==="Between"){grailsFilterPane.showElement("between-span-"+n+"To")}else{grailsFilterPane.hideElement("between-span-"+n+"To")}}var s=n+"-container";if(i.value=="IsNull"||i.value=="IsNotNull"){grailsFilterPane.hideElement(t);if(document.getElementById(s)){grailsFilterPane.hideElement(s)}}else{grailsFilterPane.showElement(t);if(document.getElementById(s)){grailsFilterPane.showElement(s)}}}}},selectDefaultOperator:function(e){var t=document.getElementById(e);if(t&&t.selectedIndex<=0){t.selectedIndex=1}}}